import { useState, useEffect } from "react";
import { Link, Navigate } from "react-router-dom";
import axios from "axios";

const PerintahList = () => {
  const [pembayaran, setPembayaran] = useState([]);
  const [msg, setMsg] = useState([]);

  useEffect(() => {
    getPembayaran();
  }, []);

  const getPembayaran = async () => {
    const response = await axios.get("http://localhost:5000/pembayaran");
    setPembayaran(response.data);
  };

  const deletePembayaran = async (perintahId) => {
    await axios.delete(`http://localhost:5000/pembayaran/${pembayaranId}`);
    getPembayaran();
  }

  return (
    <div className="container">
      <div className="container-perintah">
        <h1>Perintah</h1>
        <Link to="/perintah/add">
          <button className="btn-add">Add New</button>
        </Link>
        <table>
          <thead>
            <tr>
              <th className="index">No</th>
              <th>Nama Pembayaran</th>
              <th>Nominal</th>
              <th>Created By</th>
              <th className="action">Actions</th>
            </tr>
          </thead>
          <tbody>
            {pembayaran.map((pembayaran, index) => (
              <tr key={pembayaran.uuid}>
                <td>{index + 1}</td>
                <td>{pembayaran.nama}</td>
                <td>{pembayaran.nominal}</td>
                <td>{pembayaran.user.name}</td>
                <td>
                  <button className="btn-merah"
                    onClick={() => deletePembayaran(pembayaran.uuid)}
                  >Delete
                  </button>
                  <Link
                    to={`/perintah/edit/${pembayaran.uuid}`}
                  > <button className="btn-kuning">Edit</button>
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

    </div>
  )
}

export default PerintahList
