import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const PembayaranWarga = () => {
    const [data, setData] = useState([]);

    useEffect(() => {
        getData();
    }, []);

    const getData = async () => {
        const response = await axios.get("http://localhost:5000/pembayaran");
        setData(response.data);
    };

    const bayar = async (e) => {
        e.preventDefault();
        try {
            await axios.patch(`http://localhost:5000/pembayaran/${id}`, {
                statusPembayaran: true
            });
            navigate("/pembayaran");
        } catch (error) {
            if (error.response) {
                setMsg(error.response.data.msg);
            }
        }
    };

    return (
        <div className="container">
            <div className="container-perintah">
                <h1 className="">Approve List</h1>
                <table>
                    <thead>
                        <tr>
                            <th>No</th>
                            <th>Nama Pembayaran</th>
                            <th>Nominal</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((data, index) => (
                            <tr key={data.uuid}>
                                <td>{index + 1}</td>
                                <td>{data.nama}</td>
                                <td>{data.nominal}</td>
                                <td className="action">
                                    <button className="btn-acc" onClick={bayar}>Bayar</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default PembayaranWarga;
