import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const ApproveList = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    const response = await axios.get("http://localhost:5000/approve");
    setUsers(response.data);
  };

  return (
    <div className="container">
      <div className="container-user">
        <h1 className="">Approve List</h1>
        <div>
          <Link to="/users/add">
          <button className="btn-add">Add New</button>
        </Link>
        </div>
        <table>
          <thead>
            <tr>
              <th>No</th>
              <th>Keterangan</th>
              <th>Nominal</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={user.uuid}>
                <td>{index + 1}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.role}</td>
                <td className="action">
                  <Link
                    to={`/users/edit/${user.uuid}`}
                  >
                    <button className="btn-kuning">Edit</button>
                  </Link>
                  <button
                    onClick={() => deleteUser(user.uuid)}
                    className="btn-merah"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ApproveList;
